import React from 'react';
import data from './data.json';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = { data };
  }

  handleCellClick =(id) => {
    // Your logic for hanlding the cell click and redirection to details page
  }

  render() {
    return (
      <div className="info-flow">
          {this.state.data.map(item => (
            <div className="cell" key={item.id} onClick={()=> this.handleCellClick(item.id)}>
                <h2>{item.title}</h2>
                <p>{item.description}</p>
            </div>
          ))}
      </div>
    );
  }
}

export default App;
